	function inViewWay2(dom, view) {
		// console.log(dom,view)
		if (dom.offsetParent === null) { return false; }
		let Left = lazyload.getPositionLeft(dom);
		let Top = lazyload.getPositionTop(dom);
		return Left >= 0 && Top >= 0 && Left < view['lookWidth'] && Top < view['lookHeight'];
	}
	//第三种使用IntersectionObserver api implement in the top



	// }
	// maybe here you can give a flag and then it maybe will be fast
	/**
	 * @description this function use to change dom Element src.
	 * @param {HTML Element} domElement 
	 */
	function srcChange(domElement) {
		// 事实上。srcset本来就是可变的。
		// 我们也可以对当前的src做占位处理，然后再操作。实际上与处理img是一致的。。。
		console.log(supporSrcset , Settings.srcset , domElement.getAttribute(Settings.srcsetTag) , (domElement instanceof HTMLImageElement))
		if (supporSrcset && Settings.srcset && domElement.getAttribute(Settings.srcsetTag) && (domElement instanceof HTMLImageElement)) {

			// 	let DPI = window.devicePixelRatio;
			// console.log(domElement.getAttribute(Settings.srcsetTag))
			// 事实上下面的代码看起来是重复很多的。我们实际上可以用一个生成器来生产它们。
			domElement.setAttribute('srcset', domElement.getAttribute(Settings.srcsetTag));
			let srcLocation = domElement.getAttribute(Settings.srcsetTag);
			onerror = (e,domElement) => {
				domElement.setAttribute('src', Settings.error_picture);
				Settings.callback(e, domElement, 'error');
			}
			onloading = (domElement) => {
				domElement.setAttribute('src', Settings.loading_picture);
			}
			proxyImgChange(domElement, srcLocation, onloading, onerror)
		} else if (Settings.background && domElement.getAttribute(Settings.backgroundTag)) {
			let srcLocation = domElement.getAttribute(Settings.backgroundTag);
			onerror = (e, domElement) => {
				domElement.style.backgroundImage = 'url(' + Settings.error_picture + ')';
				Settings.callback(e, domElement, 'error');
			}
			onloading = (domElement) => {
				domElement.style.backgroundImage = 'url(' + Settings.loading_picture + ')';
			}
			proxyImgChange(domElement, srcLocation, onloading, onerror)
		} else {
			let srcLocation = domElement.getAttribute(Settings.imgTag);
			onerror = (e, domElement) => {
				domElement.src = Settings.error_picture;
				Settings.callback(e, domElement, 'error');
			}
			onloading = (domElement) => {
				domElement.src = Settings.loading_picture;
			}
			proxyImgChange(domElement, srcLocation, onloading, onerror)

		}
		if (!!Settings.deleleData) {
			domElement.removeAttribute(Settings.sersetTag);
			domElement.removeAttribute(Settings.backgroundTag);
			domElement.removeAttribute(Settings.imgTag);
		}
	}


	function proxyImgChange(elementObject, src, loading, error) {
		//here can use a global Image object. but in here is quickly.....
		let img = new Image();
		img.src = src;
		if ((elementObject instanceof HTMLImageElement)) {
			// console.log(elementObject.src)
			// elementObject.onerror = function(){
			// };
			img.onerror = (e) => {
				error(e, elementObject);
			}
			if (isFunction(loading) && Settings.isStartLoadingPic) {
				loading(elementObject);
			}
			img.onload = function () {
				elementObject.src = src;
			}
		} else {
			console.error(`check ${elementObject} type plz....`)
		}

	}

